package com.Day1_Assignment;

import java.util.Scanner;

public class SixteenthProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = Integer.parseInt(args[0]);
		int i=1;
		while(i<=10)
		{
			int y= (x * i);
			System.out.println(y);
			i++;
		}

	}

}
